import React, { useState } from 'react';
import axios from 'axios';

const Home = () => {
    const [loading, setLoading] = useState(false);
    const [videoUrl, setVideoUrl] = useState("");
    const [text, setText] = useState(""); // Input state for user text

    const generateVideo = async () => {
        if (!text.trim()) {
            alert("Please enter text to generate video.");
            return;
        }

        setLoading(true);
        try {
            const response = await axios.post("http://localhost:8000/generate/", { text });

            if (response.data.video_url) {
                setVideoUrl(`http://localhost:8000${response.data.video_url}`);
            }
        } catch (error) {
            console.error("Error generating video:", error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={{ textAlign: "center", padding: "20px" }}>
            <h1>Text-to-Video Generator</h1>

            <input
                type="text"
                placeholder="Enter text for video..."
                value={text}
                onChange={(e) => setText(e.target.value)}
                style={{ padding: "10px", margin: "10px", width: "60%" }}
            />

            <button onClick={generateVideo} disabled={loading}>
                {loading ? "Generating..." : "Generate Video"}
            </button>

            {videoUrl && (
                <div style={{ marginTop: "20px" }}>
                    <h3>Download your video:</h3>
                    <a href={videoUrl} download="generated_video.mp4">
                        Click to Download
                    </a>
                    <video src={videoUrl} controls style={{ width: "500px", height: "300px", display: "block", margin: "20px auto" }} />
                </div>
            )}
        </div>
    );
};

export default Home;
